import React, { useState } from 'react';
import '../App.css';

function ValueButtons() {
    const [buttons, setButtons] = useState(
            [{
            text: 'X',
            value: 10,
            color: 'gold'
        },
        {
            text: '10',
            value: 10,
            color: 'gold'
        },
        {
            text: '9',
            value: 9,
            color: 'gold'
        },
        {
            text: '8',
            value: 8,
            color: 'red'
        },
        {
            text: '7',
            value: 7,
            color: 'red'
        },
        {
            text: '6',
            value: 6,
            color: 'blue'
        },
        {
            text: '5',
            value: 5,
            color: 'blue'
        },
        {
            text: '4',
            value: 4,
            color: 'black'
        },
        {
            text: '3',
            value: 3,
            color: 'black'
        },
        {
            text: '2',
            value: 2,
            color: 'white'
        },
        {
            text: '1',
            value: 1,
            color: 'white'
        },
        {
            text: 'M',
            value: 0,
            color: 'green'
        },
        {
            text: 'CLEAR',
            value: 0,
            color: 'blue'
        },
        {
            text: 'NEXT END',
            value: 0,
            color: 'blue'
        },
        {
            text: 'RESET',
            value: 0,
            color: 'grey'
        }
    ])
    return (
        <div className = "button-group">
            { buttons.map( (button) => {
                return <button className = {button.color} value = {button.value}>{button.text}</button>
            })}
        </div>
    );
}

export default ValueButtons;
