import React, { useState } from 'react';
import '../App.css';

function ValueButtons() {
    const [buttons, setButtons] = useState({
        xButton: {
            text: 'X',
            value: 10,
            color: 'gold'
        },
        tenButton: {
            text: '10',
            value: 10,
            color: 'gold'
        },
        nineButton: {
            text: '9',
            value: 9,
            color: 'gold'
        },
        eightButton: {
            text: '8',
            value: 8,
            color: 'red'
        },
        sevenButton: {
            text: '7',
            value: 7,
            color: 'red'
        },
        sixButton: {
            text: '6',
            value: 6,
            color: 'blue'
        },
        fiveButton: {
            text: '5',
            value: 5,
            color: 'blue'
        },
        fourButton: {
            text: '4',
            value: 4,
            color: 'black'
        },
        threeButton: {
            text: '3',
            value: 3,
            color: 'black'
        },
        twoButton: {
            text: '2',
            value: 2,
            color: 'white'
        },
        oneButton: {
            text: '1',
            value: 1,
            color: 'white'
        },
        missButton: {
            text: 'M',
            value: 0,
            color: 'green'
        },
        clearButton: {
            text: 'CLEAR',
            value: 0,
            color: 'blue'
        },
        nextButton: {
            text: 'NEXT END',
            value: 0,
            color: 'blue'
        },
        resetButton: {
            text: 'RESET',
            value: 0,
            color: 'grey'
        }
    })
    return (
        <div className = "button-group">
            { buttons.map( (button) => {
                return <button className = {button.color} value = {button.value}>{button.text}</button>
            })}
        </div>
    );
}

export default ValueButtons;
