import React, {useState} from 'react';
import './App.css';
import ValueButtons from './components/ValueButtons';
import ScoreDisplay from './components/ScoreDisplay';
import SelectLevel from './components/SelectLevel';
import LevelRules from './components/LevelRules';



function App() {
  //Initial state. Later will incorporate a button in UI for changing level
  const [level, setLevel] = useState('elite')
  const [appState, setAppState] = useState(
    [
      {
        displayText: [],
        endValues: [],
        endScore: 0,
        totalScore: 0
      }
    ]);

  function updateScroll () {
    const element = document.getElementById('score-display');
    element.scrollTop = (element.scrollHeight);
  }

    //Initialize newState
  let newState = []
  let neutral = 0

  function decrementScore () {
    newState[newState.length-1].endScore--
    newState[newState.length-1].totalScore--
  }

  function incrementScore() {
    newState[newState.length-1].endScore++
    newState[newState.length-1].totalScore++
  } 

  //Function to handle scoring for both adding an arrow value or deleting one.
  function handleInput (level, value, opt1, opt2) {
    switch (level) {
      case 'beginner':
        if (value > 6) {
          opt1()
        } else if (value === 6) {
          break
        } else if (value < 6) {
          opt2()
        }
        break;
      case 'intermediate':
        if (value > 7) {
          opt1()
        } else if (value === 7) {
          break
        } else if (value < 7) {
          opt2()
        }
        break;
      case 'advanced':
        if (value > 8) {
          opt1()
        } else if (value === 8) {
          break
        } else if (value < 8) {
          opt2()
        }
        break;
      case 'elite':
        if (value > 9) {
          opt1()
        } else if (value === 9) {
          break
        } else if (value < 9) {
          opt2()
        }
        break;
      default:
        return
    }
  }

  function setRules(level) {
    if (level === 'beginner') {
        neutral = 6;
    } else if (level === 'intermediate') {
        neutral = 7;
    } else if (level === 'advanced') {
        neutral = 8;
    } else if (level === 'elite') {
        console.log('here')
        neutral = 9;
    }
    var addOne = neutral + 1;
    var subOne = neutral - 1;
  }

  const handleLevel = (e) => {
    e.preventDefault()
    let options = document.getElementsByName('level') 
    for (var i=0; i<options.length; i++) {
      if(options[i].checked) {
          const newLevel = options[i].value;
          setLevel(newLevel)
      } else {
        continue
      }
    setRules(level)
    console.log(neutral)
    }
  }


  //Function to handle any button that is clicked. This could probably be broken down into
  //small functions in the future.
  const handleButton = (e) => {
    //Code that runs when next end button is pressed
    if (e.value === "NEXT END"){
      //sort previous end to be displayed in descending order
      appState[appState.length-1].endValues.sort((a,b) => b-a)
      newState = [...appState]
      const lastTotal = newState[newState.length-1].totalScore
      const nextEnd = {displayText: [], endValues: [], endScore: 0, totalScore: lastTotal ,}
      newState.push(nextEnd)
      setAppState(newState)


      //Code that runs when an arrow value is pressed
    } else if (!isNaN(e.value)) {
      newState = [...appState]
      updateScroll()
      if (newState[newState.length-1].endValues.length < 6) {
        newState[newState.length-1].endValues.push(parseInt(e.value, 10))
        newState[newState.length-1].displayText.push(e.innerHTML)
        handleInput(level, e.value, incrementScore, decrementScore)
        setAppState(newState)
      } else {
        return
      }

      //Code that runs when clear is pressed
    } else if (e.value === 'CLEAR') {
      newState = [...appState]
      const deletedValue = newState[newState.length-1].endValues.pop()
      newState[newState.length-1].displayText.pop()
      handleInput(level, deletedValue, decrementScore, incrementScore)
      setAppState(newState)
    }
  }


  return (
    <div className="App">
      <div className = "scorecard">
        <ScoreDisplay appState = {appState} />
        <ValueButtons handleButton = {handleButton} />
      </div>
      <div className = "control-panel">
        <h3>Control Panel</h3>
        <SelectLevel handleLevel = {handleLevel} />
        <LevelRules level = {level} />
      </div>
    </div>
  );
}

export default App;


