import React from 'react';


function getSelected (target, handleLevel) {
    console.log(target)
    let options = document.getElementsByName('level') 
    for (var i=0; i<options.length; i++) {
        if(options[i].checked) {
            const newLevel = options[i].value;
            console.log(newLevel)
            handleLevel(newLevel)
        }
    }
}

export default function SelectLevel(props) {
    return (
        <div className = "form-container">
            <form className = "level-form">

                <label className = "form-label" htmlFor = "levels">Choose your level:</label><br/>
                <div className = "form-option">
                    <input type = "radio" id = "beginner" name = "level" value = "beginner" />
                    <label htmlFor = "beginner">Beginner</label>
                </div>
                <div className = "form-option">
                    <input type = "radio" id = "intermediate" name = "level" value = "intermediate" />
                    <label htmlFor = "intermediate">Intermediate</label>
                </div>
                <div className = "form-option">
                    <input type = "radio" id = "advanced" name = "level" value = "advanced" />
                    <label htmlFor = "advanced">Advanced</label>
                </div>
                <div className = "form-option">
                    <input type = "radio" id = "elite" name = "level" value = "elite" />
                    <label htmlFor = "elite">Elite</label>
                </div>
                <button className = "level-submit" onClick ={(e) => props.handleLevel(e)}>Submit</button>

            </form>
        </div>
    )
}
